// Importations nécessaires
import React, { useState, useContext, createContext } from "react";
import { MapContainer, TileLayer, GeoJSON } from "react-leaflet";
import "leaflet/dist/leaflet.css";

// Contexte pour gérer les régions
const RegionContext = createContext();

const RegionProvider = ({ children }) => {
  const [regions, setRegions] = useState([]);

  const addRegion = (region) => {
    setRegions((prev) => {
      const existing = prev.find((r) => r.name === region.name);
      if (existing) {
        return prev.map((r) =>
          r.name === region.name
            ? { ...r, intensity: r.intensity + region.intensity }
            : r
        );
      }
      return [...prev, region];
    });
  };

  return (
    <RegionContext.Provider value={{ regions, addRegion }}>
      {children}
    </RegionContext.Provider>
  );
};

// Composant principal
const Dashboard = () => {
  return (
    <RegionProvider>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}>
        <h1>Tableau de Bord</h1>
        <RegionForm />
        <RegionMap />
      </div>
    </RegionProvider>
  );
};

// Formulaire pour ajouter des régions
const RegionForm = () => {
  const { addRegion } = useContext(RegionContext);
  const [formData, setFormData] = useState({ name: "", intensity: 1 });

  const handleSubmit = (e) => {
    e.preventDefault();
    addRegion({ name: formData.name, intensity: parseInt(formData.intensity) });
    setFormData({ name: "", intensity: 1 });
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
      <input
        type="text"
        placeholder="Nom de la région"
        value={formData.name}
        onChange={(e) => setFormData({ ...formData, name: e.target.value })}
        required
      />
      <input
        type="number"
        placeholder="Intensité"
        value={formData.intensity}
        onChange={(e) =>
          setFormData({ ...formData, intensity: e.target.value })
        }
        min="1"
        required
      />
      <button type="submit">Ajouter Région</button>
    </form>
  );
};

// Carte pour afficher les régions
const RegionMap = () => {
  const { regions } = useContext(RegionContext);

  // Exemple de GeoJSON pour les régions
  const geojsonData = {
    type: "FeatureCollection",
    features: regions.map((region) => ({
      type: "Feature",
      properties: { name: region.name, intensity: region.intensity },
      geometry: {
        type: "Polygon",
        coordinates: [
          [
            [-1.5, 47.5],
            [-1.6, 47.5],
            [-1.6, 47.6],
            [-1.5, 47.6],
            [-1.5, 47.5],
          ],
        ],
      },
    })),
  };

  const getColor = (intensity) => {
    return intensity > 5 ? "#800026" : intensity > 3 ? "#BD0026" : "#FFEDA0";
  };

  const onEachFeature = (feature, layer) => {
    const { name, intensity } = feature.properties;
    layer.setStyle({
      fillColor: getColor(intensity),
      fillOpacity: 0.7,
      color: "#000",
      weight: 1,
    });
    layer.bindPopup(`<strong>${name}</strong><br>Intensité: ${intensity}`);
  };

  return (
    <MapContainer
      style={{ height: "500px", width: "800px" }}
      center={[47.5, -1.5]}
      zoom={6}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      <GeoJSON data={geojsonData} onEachFeature={onEachFeature} />
    </MapContainer>
  );
};

export default Dashboard;
