import React, { useState, useContext, createContext } from 'react';
import { MapContainer, TileLayer, GeoJSON, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import regionsGeoJSON from './france-regions.json';

// Correction de l'icône par défaut des marqueurs
const customIcon = new L.Icon({
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  iconSize: [30, 45],
  iconAnchor: [15, 45],
  popupAnchor: [1, -34],
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
  shadowSize: [41, 41],
});

// Contexte pour gérer les régions
const RegionContext = createContext();

const RegionProvider = ({ children }) => {
  const [regions, setRegions] = useState({});

  const incrementRegion = (regionName) => {
    setRegions((prev) => ({
      ...prev,
      [regionName]: (prev[regionName] || 0) + 1,
    }));
  };

  return (
    <RegionContext.Provider value={{ regions, incrementRegion }}>
      {children}
    </RegionContext.Provider>
  );
};

const Dashboard = () => {
  return (
    <RegionProvider>
      <div
        style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          fontFamily: 'Arial, sans-serif',
        }}
      >
        <h1>Carte des Régions Françaises</h1>
        <RegionForm />
        <RegionMap />
      </div>
    </RegionProvider>
  );
};

const RegionForm = () => {
  const { incrementRegion } = useContext(RegionContext);
  const [selectedRegion, setSelectedRegion] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedRegion) {
      incrementRegion(selectedRegion);
      setSelectedRegion('');
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        display: 'flex',
        gap: '10px',
        marginBottom: '20px',
      }}
    >
      <select
        value={selectedRegion}
        onChange={(e) => setSelectedRegion(e.target.value)}
        style={{ padding: '5px', width: '200px' }}
      >
        <option value="">Sélectionnez une région</option>
        {regionsGeoJSON.features.map((region) => (
          <option key={region.properties.nom} value={region.properties.nom}>
            {region.properties.nom}
          </option>
        ))}
      </select>
      <button
        type="submit"
        disabled={!selectedRegion}
        style={{
          padding: '5px 10px',
          backgroundColor: selectedRegion ? '#4CAF50' : '#cccccc',
        }}
      >
        Ajouter
      </button>
    </form>
  );
};

const RegionMap = () => {
  const { regions } = useContext(RegionContext);

  const getColor = (regionName) => {
    const count = regions[regionName] || 0;
    const intensity = Math.min(count * 20, 255); // Limite à 255
    return `rgb(255, ${255 - intensity}, ${255 - intensity})`;
  };

  const onEachFeature = (feature, layer) => {
    const regionName = feature.properties.nom;
    const count = regions[regionName] || 0;

    layer.setStyle({
      fillColor: getColor(regionName),
      fillOpacity: 0.7,
      color: '#000',
      weight: 2, // Augmentation de l'épaisseur des contours
    });

    const popupContent = `
      <div style="text-align: center;">
        <strong>${regionName}</strong><br>
        Nombre d'ajouts: ${count}
      </div>
    `;
    layer.bindPopup(popupContent);
  };

  return (
    <MapContainer
      style={{ height: '600px', width: '100%', maxWidth: '1000px' }}
      center={[46.5, 2.5]}
      zoom={5}
      maxZoom={10}
      minZoom={4}
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <GeoJSON
        key={JSON.stringify(regions)}
        data={regionsGeoJSON}
        onEachFeature={onEachFeature}
        style={() => ({ weight: 3 })} // Élargit les bordures
      />
      {regionsGeoJSON.features.map((feature) => {
        const regionName = feature.properties.nom;
        const count = regions[regionName] || 0;
        if (count > 0) {
          const center = feature.geometry.coordinates[0][0]; // Simplification, utilisez turf.js pour plus de précision

          // Ajout d'un marqueur de texte
          return (
            <>
              <Marker key={regionName} position={[center[1], center[0]]} icon={customIcon}>
                <Popup>
                  <strong>{regionName}</strong>: {count}
                </Popup>
              </Marker>
              <div
                style={{
                  position: 'absolute',
                  transform: 'translate(-50%, -50%)',
                  left: `${center[0]}px`,
                  top: `${center[1]}px`,
                  backgroundColor: 'rgba(255, 255, 255, 0.8)',
                  border: '1px solid black',
                  padding: '2px 4px',
                  borderRadius: '3px',
                }}
              >
                {count}
              </div>
            </>
          );
        }
        return null;
      })}
    </MapContainer>
  );
};

export default Dashboard;
